import React from 'react';

const Filter = ({ showUnDone, showDone, showAll }) => {
        return (
     		<div className = 'filter'>
                <button
                    onClick = { showUnDone }
                    // style = {{ backgroundColor : showDone ? 'grey' : 'white' }}
                >
                    show done
                </button> 
                <button
                    onClick = { showAll }
                >
                    All
                </button>
                <button
                    onClick = { showDone }
                >
                    show undone
                </button>
            </div>
        );
}
export default Filter;
