import React , { Fragment } from 'react';
import Header from './header';
import Footer from './footer';
import Form from './form';
import List from './list';
import Filter from './filter';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
        	tasks : [],
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isDone = this.isDone.bind(this);
    }

    handleChange = (event) => {
    	let inputName = event.target.name;
    	this.setState({[inputName] : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let task = this.state.taskInput;
    	let tasks = [ ...this.state.tasks, { name : task, isDone : false, isRemove : false} ];
    	this.setState({tasks, taskInput : ""})
    }
    isDone = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isDone = !this.state.tasks[id].isDone;
        this.setState({tasks});
    }
    removeTask = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isRemove = true;
        this.setState({tasks});
    }
    removeDone = () => {
        let tasks = [...this.state.tasks];

    }


    render() {

    	const list = this.state.tasks
	    	.map((item, index) => {
                if (!item.isRemove) {
        	    	return 	<List
                			    key = {index}
                			    task = {item.name}
                			    isDone = {item.isDone}
                			    button = { () => this.isDone(index) }
                                removeTask = { () => this.removeTask(index) }
            	    	    />   
                }
	    	})

        return (
        	<Fragment>
	            <Header />
	            <section>
                    <Form
                        taskInput = { this.state.taskInput }
                        submit = {this.handleSubmit}
                        handleChange = {this.handleChange}
                    />
                    <ul>
	                    {list}
                    </ul>
                    <Filter />
	            </section>
	            <Footer />
            </Fragment>
        );
    }
}

export default Home;






















