import React, { Component } from 'react';

class List extends Component {

    render() {

		const { button, isDone, task, removeTask } = this.props;

    	let buttonSwitch = null;
    	if (isDone) {
    		buttonColor = "reset done"
    	}
    	else {
    		buttonColor = "mark as done"
    	}
        return (
        	<div className = 'list' >
	           	<li 
	           		data-done = {isDone} 
	           		style = { { backgroundColor : isDone === true ? 'green' : 'grey' } } 
	           	>
	           		{ task }
	           	</li>
	           	<div className = 'buttonList'>
		           	<button  
		           		type = 'button'
		           		onClick = { button }  
		           	>
		           		{ buttonColor }
		           	</button>
		           	<button  
		           		type = 'button'
		           		onClick = { removeTask }  
		           	>
		           		remove
		           	</button>
	           	</div>
	        </div>
        );
    }
}

export default List;
