import React from 'react';
import Header from './header';
import Footer from './footer';
import List from './List';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
        	tasks : [],
        	isDelete : true
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.delete = this.delete.bind(this);
    }

    handleChange = (event) => {
    	let inputName = event.target.name;
    	this.setState({[inputName] : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let task = this.state.taskInput;
    	let tasks = [ ...this.state.tasks, { name : task, done : true} ];

    	this.setState({tasks, taskInput : ""})
    }
    delete = () => {
    	console.log("hello");
    	let index = this.state.tasks[index];

    }


    render() {
    	const tasks = this.state.tasks.done;

    	if(tasks.done){
    		const list = this.state.tasks
    		.map((item, index) => {
    			return <List
    					key = {index}
    					done = {item.done}
    					task = {item.name}
    					delete = {this.delete}
    					/>
    		})
    	}


        return (
        	<div>
	            <Header />
	            <section>
	            	<form onSubmit = {this.handleSubmit}>
	            		<label>ajouter une t√¢che : </label>
	            		<input
	            			type = 'text'
	            			value = {this.state.taskInput} 
	            			onChange = {this.handleChange}
	            			name = 'taskInput'
	            		 />
	            		 <button type = 'submit'>Ajouter</button>
	            	</form>
	            	<article>
	            		<ul>
	            			{list}
	            		</ul>
	            	</article>
	            </section>
	            <Footer />
            </div>
        );
    }
}

export default Home;
