import Header from './header';
import Footer from './footer';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	inputValue : ""
        }
    }




    render() {
        return (
        	<div>
	            <Header />
	            <section>
	            	<form>
	            		<label>Votre t√¢che :</label>
	            		<input
	            			type = 'text'
	            			value = {this.state.inputValue} 
	            			onChange = {this.handleChange}
	            		 />
	            	</form>
	            </section>
	            <Footer />
            </div>
        );
    }
}

export default Home;
