import React from 'react';
import Header from './header';
import Footer from './footer';
import Form from './form';
import List from './List';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
        	tasks : [],

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isDone = this.isDone.bind(this);
    }

    handleChange = (event) => {
    	let inputName = event.target.name;
    	this.setState({[inputName] : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let task = this.state.taskInput;
    	let tasks = [ ...this.state.tasks, { name : task, isDone : false, isRemove : false} ];

    	this.setState({tasks, taskInput : ""})
    }
    isDone = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isDone = !this.state.tasks[id].isDone;
        this.setState({tasks});
    }
    removeTask = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isRemove = true;
        this.setState({tasks});
    }


    render() {

    	const list = this.state.tasks
	    	.map((item, index) => {
                if (!item.isRemove) {
        	    	return 	<List
                			    key = {index}
                			    task = {item.name}
                			    isDone = {item.isDone}
                			    button = { () => this.isDone(index) }
                                removeTask = { () => this.removeTask(index) }
            	    	    />   
                }
	    	})

        return (
        	<div>
	            <Header />
	            <section>
	            	{/* <form onSubmit = {this.handleSubmit}>
                                            <label>ajouter une t√¢che : </label>
                                            <input
                                                type = 'text'
                                                value = {this.state.taskInput} 
                                                onChange = {this.handleChange}
                                                name = 'taskInput'
                                             />
                                             <button type = 'submit'>Ajouter</button>
                                        </form> */}
	            	<article>
                    <Form
                        submit = {this.handleSubmit}
                        taskInput = {this.handleChange}
                     />
                        <ul>
	                        {list}
                        </ul>
	            	</article>
	            </section>
	            <Footer />
            </div>
        );
    }
}

export default Home;
