import React , { Fragment } from 'react';
import Header from './header';
import Footer from './footer';
import Form   from './form';
import List   from './list';
import Filter from './filter';
import '../App.css';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput  : "",
        	tasks      : [],
            error      : null
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isDone       = this.isDone.bind(this);
        this.showUnDone   = this.showUnDone.bind(this);
        this.showDone     = this.showDone.bind(this);
        this.removeTask   = this.removeTask.bind(this);
        this.showAll      = this.showAll.bind(this);
    };

    componentDidMount () {
        let tasks = [ ...this.state.tasks ];
        tasks = JSON.parse(localStorage.getItem('tasks'));
        this.setState({ tasks });
    }
    handleChange = (event) => {
        let taskInput = event.target.name;
    	this.setState({ [taskInput] : event.target.value });
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let taskInput = [ ...this.state.taskInput ];
        if (taskInput.length > 0) {
        	let tasks = [ ...this.state.tasks,
                            { 
                                id       : `tache numÃ©ro:${Date.now()}`,
                                name     : taskInput,
                                isDone   : false,
                                isRemove : false
                            }
                        ];
        localStorage.setItem('tasks' , JSON.stringify(tasks));
        this.setState({ tasks, taskInput : "", error : false });

        } else {
            this.setState({error : true});
        }
    }
    isDone = (id) => {
        let tasks = [ ...this.state.tasks ];
        tasks.map( item => {
            if (id === item.id) {
                item.isDone = !item.isDone;
            }
        })


        localStorage.setItem('tasks' , JSON.stringify(tasks));

        this.setState({ tasks });
        // tasks[id].isDone = !tasks[id].isDone;
        // localStorage.setItem('tasks' , JSON.stringify(tasks));
    }
    removeTask = (id) => {
        let tasks = [ ...this.state.tasks ];
        tasks.map( item => {
            if (id === item.id) {
               spilce(item.id);
            }
        })
        this.setState({tasks});
        localStorage.setItem('tasks' , JSON.stringify(tasks));
    }
    showUnDone = () => {
        // this.componentDidMount();

        this.setState({ tasks : this.state.tasks.filter( item => item.isDone === true) });
    }
    showDone = () => {
        // this.componentDidMount();
        const tasks = this.state.tasks.map( item => {
                                                if(item.isDone === false){
                                                   this.state.tasks = null;
                                                }
                                            });
        this.setState({tasks});
    }
    showAll = () => {
        this.componentDidMount();
    }

    render() {

    	const list = this.state.tasks
	    	.map((item, index) => {
                if (!item.isRemove) {
        	    	return 	<List
                			    key        = {item.id}
                			    task       = {item.name}
                			    isDone     = {item.isDone}
                			    button     = { () => this.isDone(item.id) }
                                removeTask = { () => this.removeTask(item.id) }
            	    	    />   
                }
	    	})

        return (
        	<Fragment>
	            <Header />
	            <section>
                    <Form
                        taskInput    = { this.state.taskInput }
                        submit       = { this.handleSubmit }
                        handleChange = { this.handleChange }
                    />
                    { this.state.error ? <p className = 'alert'>Veuillez insÃ©rer une tÃ¢che</p> :  null}
                    <ul>
	                    {list}
                    </ul>
                    <Filter
                        showDone   = {this.showDone}
                        showAll    = {this.showAll}
                        showUnDone = {this.showUnDone}
                    />
	            </section>
	            <Footer />
            </Fragment>
        );
    }
}

export default Home;
