import React , { Fragment } from 'react';
import Header from './header';
import Footer from './footer';
import Form from './form';
import List from './list';
import Filter from './filter';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
            error : null,
        	tasks : [],
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isDone       = this.isDone.bind(this);


    }

    handleChange = (event) => {

    	this.setState({taskInput : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let task = this.state.taskInput;
        if (task.length > 1) {
        	let tasks = [ ...this.state.tasks,
                            { 
                                id : Date.now(),
                                name    : task,
                                isDone   : false,
                                isRemove : false
                            }
                        ];
        	this.setState({ tasks, taskInput : "", error : false });
        } else {
            this.setState({error : true});
        }
    }
    isDone = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isDone = !this.state.tasks[id].isDone;
        this.setState({ tasks });
    }
    removeTask = (id) => {
        let tasks = [ ...this.state.tasks ];
        this.state.tasks[id].isRemove = true;
        this.setState({tasks});
    }



    render() {

        if (this.state.error === true) {
            return ( <Filter />)
        }
    	const list = this.state.tasks
	    	.map((item, index) => {
                if (!item.isRemove) {
        	    	return 	<List
                			    key = {item.id}
                			    task = {item.name}
                			    isDone = {item.isDone}
                			    button = { () => this.isDone(index) }
                                removeTask = { () => this.removeTask(item.id) }
            	    	    />   
                }
	    	})

        return (
        	<Fragment>
	            <Header />
	            <section>
                    <Form
                        taskInput = { this.state.taskInput }
                        submit = {this.handleSubmit}
                        handleChange = {this.handleChange}
                    />
                    {this.state.error}
                    <ul>
	                    {list}
                    </ul>
                    <Filter
                        hideUnDone = {this.hideUnDone}
                        hideDone   = {this.hideDone}
                    />
	            </section>
	            <Footer />
            </Fragment>
        );
    }
}

export default Home;






















