import React , { Fragment } from 'react';
import Header from './header';
import Footer from './footer';
import Form   from './form';
import List   from './list';
import Filter from './filter';
import '../App.css';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
        	tasks     : [],
            error     : null
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isDone       = this.isDone.bind(this);
        this.hideUnDone   = this.hideUnDone.bind(this);
        this.hideDone     = this.hideDone.bind(this);
        this.removeTask   = this.removeTask.bind(this);
        this.showAll      = this.showAll.bind(this);
    }


    // componentDidUpdate () {
    //     let tasks = [ ...this.state.tasks ];
    //     localStorage.setItem('tasks' , JSON.stringify(tasks));
    // }

    componentDidMount () {
        let tasks = [ ...this.state.tasks ];
        tasks = JSON.parse(localStorage.getItem('tasks'));
        this.setState({ tasks })
    }
    handleChange = (event) => {
        let taskInput = event.target.name;
    	this.setState({ [taskInput] : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let taskInput = [ ...this.state.taskInput ];
        if (taskInput.length > 0) {
        	let tasks = [ ...this.state.tasks,
                            { 
                                id       : `tache numéro:${Date.now()}`,
                                name     : taskInput,
                                isDone   : false,
                                isRemove : false
                            }
                        ];
        this.setState({ tasks, taskInput : "", error : false });
        localStorage.setItem('tasks' , JSON.stringify(tasks));

        } else {
            this.setState({error : true});
        }
    }
    isDone = (id) => {
        let tasks = [ ...this.state.tasks ];
        tasks[id].isDone = !tasks[id].isDone;
        this.setState({ tasks });

        localStorage.setItem('tasks' , JSON.stringify(tasks));
    }
    removeTask = (id) => {
        let tasks = [ ...this.state.tasks ];
        tasks[id].isRemove = true;
        this.setState({tasks});

        localStorage.setItem('tasks' , JSON.stringify(tasks));
    }
    hideUnDone = () => {
        this.setState({ tasks : this.state.tasks.filter( item => item.isDone === true) });
        this.componentDidMount();
    }
    hideDone = () => {
        this.componentDidMount();
        this.setState({ tasks : this.state.tasks.filter( item => item.isDone === false) });
    }
    showAll = () => {
        let tasks = [ ...this.state.tasks ];
        tasks = JSON.parse(localStorage.getItem('tasks'))
        this.setState({  tasks });
    }

    render() {
    	// const task_from_ls = localStorage.getItem('list');
     //    const ls_js = JSON.parse(ls)
     //    console.log(lsj);

    	const list = this.state.tasks
	    	.map((item, index) => {
                if (!item.isRemove) {
        	    	return 	<List
                			    key = {item.id}
                			    task = {item.name}
                			    isDone = {item.isDone}
                			    button = { () => this.isDone(index) }
                                removeTask = { () => this.removeTask(index) }
            	    	    />   
                }
	    	})

        return (
        	<Fragment>
	            <Header />
	            <section>
                    <Form
                        taskInput = { this.state.taskInput }
                        submit = {this.handleSubmit}
                        handleChange = {this.handleChange}
                    />
                    { this.state.error ? <p className = 'alert'>Veuillez insérer une tâche</p> :  null}
                    <ul>
	                    {list}
                    </ul>
                    <Filter
                        hideDone   = {this.hideDone}
                        showAll    = {this.showAll}
                        hideUnDone = {this.hideUnDone}
                    />
	            </section>
	            <Footer />
            </Fragment>
        );
    }
}

export default Home;
