import React from 'react';
import Header from './header';
import Footer from './footer';
import List from './List';

import '../App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        	taskInput : "",
        	tasks : [],

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.button = this.button.bind(this);
    }

    handleChange = (event) => {
    	let inputName = event.target.name;
    	this.setState({[inputName] : event.target.value })
    }
    handleSubmit = (event) => {
    	event.preventDefault();
    	let task = this.state.taskInput;
    	let tasks = [ ...this.state.tasks, { name : task, isDone : false} ];

    	this.setState({tasks, taskInput : ""})
    }
    button = (id) => {
        this.state.tasks[id].done = true;
        this.setState({tasks});

    }


    render() {

    	// const hide = null
    	// const {isDelete} = this.state;
    	// if(isDelete){
    	//  hide = "hello"
    	// }

    	const list = this.state.tasks
	    	.map((item, index) => {
                if (item.isDone) {
    	    		return 	<List
    			    			key = {index}
    			    			task = {item.name}
    			    			isDone = {item.isDone}
    			    			button = { () => this.button(item) }
    			    			dataKey = {index}
    	    				/>
                }
                else {
                    return     <List
                                key = {index}
                                task = {item.name}
                                done = {item.done}
                                button = { () => this.button() }
                                dataKey = {index}
                            />
                }
	    	})

        return (
        	<div>
	            <Header />
	            <section>
	            	<form onSubmit = {this.handleSubmit}>
	            		<label>ajouter une t√¢che : </label>
	            		<input
	            			type = 'text'
	            			value = {this.state.taskInput} 
	            			onChange = {this.handleChange}
	            			name = 'taskInput'
	            		 />
	            		 <button type = 'submit'>Ajouter</button>
	            	</form>
	            	<article>

	            		<ul>
	            			{list}
	            		</ul>
	            	</article>
	            </section>
	            <Footer />
            </div>
        );
    }
}

export default Home;
